import React, {useState} from 'react'
import "./SingIn.scss"
import InputsRegistration from "../../Components/inputREG/InputsRegistration";
import {required} from "react-admin";

// Front-end
export const SingIn = ({active, setActive}) => {

    // Back-end
    const [form, setForm] = useState({
        email: '', password: ''
    })

    const changeHandler = event => {
        setForm({
            ...form, [event.target.name]: event.target.value
        })
    }

    const  registrHandler = async () => {
        try {
            const data = await request('api/auth/SingIn', 'POST', {...form})
            console.log(Date, data)
        } catch (e) {

        }
    }

    return (
        <div className={active ? "SingIn active" : "SingIn" } onClick={() => setActive(false)}>
            <div className={active ? "SingInInner active" : "SingInInner"} onClick={e => e.stopPropagation()}>
                <div className="inBlock">
                    <h1 className="EnterTitle">Вход</h1>
                    <form action="">
                        <input
                            id="email"
                            name="email"
                            placeholder="email"
                            onChange={changeHandler}
                            type="text"/>
                        <input
                            id="password"
                            name="password"
                            placeholder="password"
                            onChange={changeHandler}
                            type="password"/>
                    </form>
                    <div>
                        <button className="Button">
                            Войти
                        </button>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default SingIn;
